import './App.css';
import logo1 from './components/images/universe.jpg';
import React, { useState, useEffect, useRef } from 'react';

import { Link } from 'react-router-dom';
import io from 'socket.io-client'; 
import axios from 'axios'; 

function Home() {
  const [counters, setCounters] = useState({
    totalProjects: 0,
    universitiesContributing: 0,
  });
  const [tableData, setTableData] = useState([
    { id: 1, name: 'Anna University', projects: 200, colleges: 1002 },
    { id: 2, name: 'B University', projects: 200, colleges: 1002 },
    { id: 3, name: 'C University', projects: 200, colleges: 1002 },
    { id: 4, name: 'D University', projects: 200, colleges: 1002 },
    { id: 5, name: 'E University', projects: 200, colleges: 1002 },
    { id: 6, name: 'F University', projects: 200, colleges: 1002 },
    { id: 7, name: 'G University', projects: 200, colleges: 1002 },
  ]);
  const [searchQuery, setSearchQuery] = useState('');

  useEffect(() => {
    const socket = io('http://localhost:8000');
    socket.on('updateCounters', (data) => {
      setCounters((prevCounters) => ({
        totalProjects: prevCounters.totalProjects + data.projects,
        universitiesContributing: prevCounters.universitiesContributing + data.domains,
      }));
    });

    axios.get('/universities')
      .then((response) => {
        setTableData(response.data);
      })
      .catch((error) => console.error(error));

    return () => {
      socket.disconnect();
    };
  }, []);

  const handleSubmit = async (event) => {
    event.preventDefault();
    try {
      const formData = {
        email: "example@example.com",
        username: "exampleUser",
        password: "examplePassword",
        university: "Example University",
        college: "Example College",
        state: "Example State",
        number: "1234567890",
        acit: "AICT-12345",
        department: "Example Department",
        email1: "example@example.com",
        year: "2023",
      };

      const response = await axios.post("http://localhost:8000/signin", formData);
      setCounters((prevCounters) => ({
        totalProjects: prevCounters.totalProjects + 1,
        universitiesContributing: prevCounters.universitiesContributing + 1,
      }));
      setSearchQuery('');

      axios.get('/universities')
        .then((response) => {
          setTableData(response.data);
        })
        .catch((error) => console.error(error));

      console.log(response.data.message);
    } catch (error) {
      console.error(error);
    }
  };

   const filteredTableData = tableData.filter((row) =>
    row.name.toLowerCase().includes(searchQuery.toLowerCase())
  );
  const tableRef = useRef(null);

  const handleScrollToTable = () => {
    if (tableRef.current) {
      tableRef.current.scrollIntoView({
        behavior: 'smooth',
        block: 'start',
      });
    }
  };

  return (
    <div className="main">

      <div className="nav1">
        <nav className="navbar">
          <h1>
            Info<span>Tube</span>
          </h1>
          <ul>Home</ul>
          <Link to="/About" className='lkn'>About Us</Link>
          <ul onClick={handleScrollToTable}>Search Universities</ul>
          <Link to="./Authnom" className='lkn'>Authenticator Nomination</Link>
          <Link to="/Mainlogin" className='lkn'>Aunthenticator Login</Link>
          <ul>
            <Link to="/Login" className="btn1">
              Upload
            </Link>
          </ul>
        </nav>
        <div className="logo">
          <h1>
            Info<span>Tube</span>
          </h1>
        </div>
      </div>
      <div className="head">
        <h1>
          Let loose your mad <br />
          <span>Software</span>/Hardware <span>skills here</span>
        </h1>
        <p>
          A platform where ideas and projects taken
          up by the students of various universities/colleges can be found and
          can upload here!!..
        </p>
        <Link className='lknnw'>Student Analytics</Link>
      </div>
      <div className="container">
        <div className="uni">
          <h1>Search for<span> Universities</span>  here</h1>
        </div>
        <div className="dropdown">
          <input
          ref={tableRef}
            placeholder="Search Universities By name"
            className="inp112"
            type="name"
            value={searchQuery}
            onChange={(e) => setSearchQuery(e.target.value)}
          />
        </div>
        <div className="tv" >
          <table id="myTable">
            <thead>
              <tr>
                <th scope="col">
                  <span>S.</span> No
                </th>
                <th scope="col">
                  <span>Universities</span> Name
                </th>
                <th scope="col">Number Of <span>Projects</span></th>
                <th scope="col">Number of <span>Colleges</span></th>
                <th scope="col">View <span>Now</span></th>
              </tr>
            </thead>
            <tbody>
              {filteredTableData.map((row, index) => (
                <tr key={index}>
                  <td>{index + 1}</td>
                  <td>{row.name}</td>
                  <td>{row.projects}</td>
                  <td>{row.colleges}</td>
                  <td>
                    <Link to="/Univpage" className="lknw">
                      Click To View
                    </Link>
                  </td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>
      </div>
    </div>
  );
}

export default Home;
